function GUI:warning(text)
	echo("\n\n\n")
	decho("<255,255,0:0,0,0>WARNUNG (GUI): ")
	echo(text)
	echo("\n\n\n")
end

function GUI:error(text)
	echo("\n\n\n")
	decho("<255,155,0:0,0,0>FEHLER (GUI): ")
	echo(text)
	echo("\n\n\n")
end

function GUI:info(text)
	echo("\n\n\n")
	decho("<120,170,255:0,0,0>INFO (GUI): ")
	echo(text)
	echo("\n\n\n")
end

function color_communication()
  fg(farben.vg.ebenen)
  bg(farben.hg.ebenen)
  echo(gmcp.comm.channel.msg)
  resetFormat()
end
